import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

const socket = io('http://localhost:4000'); // 根据需要可能更改

// 样式定义
const modalStyles = {
	lineHeight: '1.8',
    display: 'flex',
    justifyContent: 'center',
    position: 'fixed',
	background: 'white',
    top: '30%',
    left: '40%',
    transform: 'translate(-30%, -50%)',
    padding: '10px',
    borderRadius: '8px',
    boxShadow: '0 0 50px rgba(0, 0, 0, 0.3)',
    zIndex: 1000, // 确保弹窗在其他内容上方
};
const overlayStyles = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 1000,
};
const App = () => {

    const [products, setProducts] = useState([]);
	const [connectionStatus, setConnectionStatus] = useState('Disconnected');
	const [isModalOpen, setIsModalOpen] = useState(false);
	const [isArchiveModalOpen, setIsArchiveModalOpen] = useState(false);
    const [newProduct, setNewProduct] = useState({ // 用于新產品的初始状态
        商品編號: '',
        商品名稱: '',
        規格: '',
        數量: 0,
        單位: '',
        到期日: '',
    });
	const [year, setYear] = useState('');
	const [month, setMonth] = useState('');
	const [userCount, setUserCount] = useState(0); // 用於存儲線上人數
	const [password, setPassword] = useState('');
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/products');
                setProducts(response.data);
                setConnectionStatus('連接成功');
            } catch (error) {
                console.error("获取產品時出錯:", error);
                setConnectionStatus('失去連線');
            }
        };
        
        fetchProducts();
		// 監聽連接的用戶數更新
		socket.on('updateUserCount', (count) => {
		setUserCount(count);
		});
		
        // 设置 socket 监听和事件处理
        socket.on('連接成功', () => {
            setConnectionStatus('連接成功');
        });

        socket.on('失去連線', () => {
            setConnectionStatus('失去連線');
        });

        socket.on('productUpdated', (updatedProduct) => {
            setProducts(prevProducts => 
                prevProducts.map(product => 
                    product.商品編號 === updatedProduct.商品編號 ? updatedProduct : product
                )
            );
        });

        // 在组件卸载時清理事件监听
        return () => {
            socket.off('連接成功');
            socket.off('失去連線');
            socket.off('productUpdated');
			socket.off('updateUserCount');
        };
    }, []);

    const updateQuantity = async (productCode, quantity) => {
        try {
            await axios.put(`http://localhost:4000/api/products/${productCode}/quantity`, { 數量: quantity });
        } catch (error) {
            console.error("更新產品時出錯:", error);
        }
    };

    const updateExpiryDate = async (productCode, expiryDate) => {
        try {
            await axios.put(`http://localhost:4000/api/products/${productCode}/expiryDate`, { 到期日: expiryDate });
        } catch (error) {
            console.error("更新到期日時出錯:", error);
        }
    };

    const handleQuantityChange = (productCode, quantity) => {
        if (quantity < 0) {
            alert("数量不能为负数。");
            return;
        }
        const updatedProducts = products.map(product =>
            product.商品編號 === productCode ? { ...product, 數量: quantity } : product 
        );

        setProducts(updatedProducts);
        updateQuantity(productCode, quantity);
    };

    const handleExpiryDateChange = (productCode, expiryDate) => {
        const updatedProducts = products.map(product =>
            product.商品編號 === productCode ? { ...product, 到期日: expiryDate } : product 
        );

        setProducts(updatedProducts);
        updateExpiryDate(productCode, expiryDate);
    };

  // 開啟新增產品的模態框，並關閉盤點歸檔的模態框
  const openProductModal = () => {
    setIsModalOpen(true);
    setIsArchiveModalOpen(false);
  };

  // 開啟盤點歸檔的模態框，並關閉新增產品的模態框
  const openArchiveModal = () => {
    setIsArchiveModalOpen(true);
    setIsModalOpen(false);
  };
	const handleArchiveSubmit = async () => {
		if (!year || !month || !password) {
		alert('請填寫所有欄位！');
		return;
		}
		
		try {
		const response = await axios.post('http://localhost:4000/api/archive', {
			year, month, password
		});

		alert('歸檔成功：' + JSON.stringify(response.data));
		setIsArchiveModalOpen(false);
		} catch (error) {
		console.error('歸檔請求失敗:', error);
		alert('歸檔失敗:請檢查網路連接狀態或稍後再試\n' + (error.response ? error.response.data : error.message));
		}
	};
    // 新增產品的状态处理
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewProduct(prevProduct => ({ ...prevProduct, [name]: value }));
    };
    // 处理新增產品逻辑
    const handleAddProduct = async () => {
        try {
            const nextNumber = products.length + 1; // 下一个編號
            const newProductWithNumber = {
                ...newProduct,
                商品編號: `新 - ${nextNumber}`,
            };
            
            const response = await axios.post('http://localhost:4000/api/products', newProductWithNumber);
            setProducts([...products, response.data]); // 更新產品列表
            setIsModalOpen(false); // 关闭弹窗
            setNewProduct({
                商品編號: '',
                商品名稱: '',
                規格: '',
                數量: 0,
                單位: '',
                到期日: '',
            }); // 重置新產品状态
        } catch (error) {
            console.error("新增產品時出錯:", error);
        }
    };

    return (
        <div>

	    {/* 導航條 */}
		<nav style={{ display: 'flex', 	width: '100%', justifyContent: 'space-between', alignItems: 'center', padding: '0px', backgroundColor: '#f5f5f5', borderBottom: '1px solid #ccc' }}>
        <h1 style={{ margin: 0 }}>庫存盤點系統</h1>
		<div style={{ margin: 0 }}>
			<label>伺服器: <strong>{connectionStatus}</strong></label><br/>
 			<label>線上使用者: <strong>{userCount}</strong>人</label>
           </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
		  <button style={{ marginRight: '10px' }} onClick={openArchiveModal}>盤點歸檔</button>

          
          <button>無效按鈕3</button>
        </div>
      </nav>
           		<div 
        style={{
          position: 'fixed',
          bottom: '20px',
          left: '20px',
          width: '100',  // 根據圖檔尺寸調整
          height: '100',
          backgroundImage: 'url(/images/button-image.gif)',  // 指向 public 資料夾內的圖檔
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          cursor: 'pointer',
        }}
        onClick={openProductModal}/>
            <table>
                <thead>
                    <tr>
                        <th>商品編號</th>
                        <th>商品名稱</th>
                        <th>數量</th>
                        <th>單位</th>
                        <th>到期日</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product.商品編號}>
                            <td>{product.商品編號}</td>
                            <td className='name'>{product.商品名稱}</td>
                            <td><input type="number" value={product.數量}  onChange={(e) => handleQuantityChange(product.商品編號, +e.target.value) } required autofocus /></td>
                            <td>{product.單位}</td>
                            <td><input type="date" value={product.到期日 ? new Date(product.到期日).toISOString().split('T')[0] : ""} onChange={(e) => handleExpiryDateChange(product.商品編號, e.target.value)} /></td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {/* 弹窗组件 */}
            {isModalOpen && (
        <div style={overlayStyles} onClick={() => setIsModalOpen(false)}>
          <div style={modalStyles} onClick={(e) => e.stopPropagation()}>
                    <div>
                        <h2>新增品项</h2>
                        <input name="商品名稱" placeholder="商品名稱" value={newProduct.商品名稱} onChange={handleInputChange} autofocus required />
                        <input name="數量" type="number" placeholder="盤點量" value={newProduct.數量} onChange={handleInputChange} required />
                        <input name="單位" placeholder="單位" value={newProduct.單位} onChange={handleInputChange} />
                        <br />
                        <label>到期日：</label>
                        <input name="到期日" type="date" value={newProduct.到期日} onChange={handleInputChange} />
                        <div>
                            <button onClick={handleAddProduct}>送出</button>
                            <button onClick={() => setIsModalOpen(false)}>關閉</button>
                        </div>
                    </div>
                </div>
				</div>
            )}
			{isArchiveModalOpen && (
        <div style={overlayStyles} onClick={() => setIsArchiveModalOpen(false)}>
          <div style={modalStyles} onClick={(e) => e.stopPropagation()}>
          <div>
            <h2>盤點歸檔</h2>
            <label>
              年份：
              <input type="number" value={year} onChange={e => setYear(e.target.value)} min="2000" max="2100" required />
            </label>
            <label>　</label>
            <label>
              月份：
              <input type="number" value={month} onChange={e => setMonth(e.target.value)} min="1" max="12" required />
            </label>
            <br />
            <label>
              管理員密碼：
              <input style={{ width: '145px'}}type="password" value={password} onChange={e => setPassword(e.target.value)} required />
            </label>
			<br />
            <div>
              <button onClick={handleArchiveSubmit}>存檔</button>
              <button onClick={() => setIsArchiveModalOpen(false)}>取消</button>
            </div>
          </div>
        </div>
		</div>
      )}
        </div>
    );
};

export default App;
