// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

// 與後端的 Socket.IO 連接
const socket = io('https://inventory.edc-pws.com');

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // 取得產品數據
    const fetchProducts = async () => {
      try {
        const response = await axios.get('https://inventory.edc-pws.com/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();

    // 監聽產品更新事件
    socket.on('productUpdated', (updatedProduct) => {
      setProducts(prevProducts =>
        prevProducts.map(product =>
           product.商品編號 === updatedProduct.商品編號 ? updatedProduct : product
        )
      );
    });

    // 清理階段
    return () => {
      socket.off('productUpdated');
    };
  }, []);

  // 更新数量的函数
  const updateQuantity = async (productCode, quantity) => {
    try {
      await axios.put(`https://inventory.edc-pws.com/api/products/${productCode}`, { quantity });
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  // 更新到期日
  const updateExpiryDate = async (productCode, expiryDate) => {
    try {
      await axios.put(`https://inventory.edc-pws.com/api/products/${productCode}`, { 到期日: expiryDate });
    } catch (error) {
      console.error("Error updating expiry date:", error);
    }
  };

  // 处理输入变化
  const handleQuantityChange = (productCode, quantity) => {
    if (quantity < 0) {
      alert("Quantity cannot be negative.");
      return;
    }
    const updatedProducts = products.map(product =>
      product.商品編號 === productCode ? { ...product, 數量: quantity } : product
    );

    setProducts(updatedProducts); // 更新本地状态
    updateQuantity(productCode, quantity); // 调用后端接口更新数量
  };

  const handleExpiryDateChange = (productCode, expiryDate) => {
    const updatedProducts = products.map(product =>
      product.商品編號 === productCode ? { ...product, 到期日: expiryDate } : product
    );

    setProducts(updatedProducts); // 更新本地状态
    updateExpiryDate(productCode, expiryDate); // 更新到期日
  };
  return (
    <div>
      <h1>庫存盤點系統</h1>
      <ul>
        <thead>
            <tr>
                <th>商品編號</th>

                <th>商品名稱</th>

                <th>規格</th>

                <th>數量</th>

                <th>單位</th>

                <th>到期日</th>
            </tr>
			</thead>
			{products.map(product => (
			<thead>
            <tr>
                <td>{product.商品編號}</td>
                <td>{product.商品名稱}</td>
                <td>{product.規格}</td>
                <td><input type="number" value={product.數量} onChange={(e) => handleQuantityChange(product.商品編號, +e.target.value)}/></td>
                <td>{product.單位}</td>
                <td><input type="date" value={product.到期日 ? new Date(product.到期日).toISOString().split('T')[0] : ''} // 格式化至YYYY-MM-DD
              onChange={(e) => handleExpiryDateChange(product.商品編號, e.target.value)}
            /></td>
			 </tr>
			</thead>
          
        ))}
      </ul>
    </div>
  );
}

export default App;
